from astropy.table import vstack, Tablefrom astropy.io import asciifields = ['COSMOS', 'EGS', 'GOODSN', 'GOODSS', 'UDS']RF_Phot_Combined_Fields = Table()for each_field in fields:    catalog = ascii.read('/Users/laurenhiggins/Dropbox/GEG_Research/Key_Parameters_Catalogs_Per_Field/Rest-Frame_Photometry_and_Photometry/CANDELS.%s.F160W.v1.rest_photom.v070715.cat'%each_field)    RF_Phot_subset = catalog[['ID','z_best', 'z_flag', 'rest1600', 'restUXbessel', 'restVbessel', 'restJpalomar', 'restHpalomar']]    RF_Phot_subset['Field_flag'] = each_field    RF_Phot_Combined_Fields = vstack([RF_Phot_Combined_Fields, RF_Phot_subset])ascii.write(RF_Phot_Combined_Fields, '/Users/laurenhiggins/Dropbox/GEG_Research/Key_Parameters_Catalogs_Per_Field/Rest-Frame_Photometry_and_Photometry/CANDELS.ALL.FIELDS.F160W.v1.Rest-Frame_Photometry.cat',             names=('ID','z_best', 'z_flag', 'rest1600', 'restUXbessel', 'restVbessel', 'restJpalomar', 'restHpalomar', 'Field_Flag'), format='csv', overwrite=True)'''2. z_best3. z_flag4. rest16006. restUXbessel8. restVbessel7. restJpalomar12.restHpalomar'''