from astropy.table import vstack, Tablefrom astropy.io import asciifields = ['COSMOS', 'EGS', 'GOODSN', 'GOODSS', 'UDS']Msol_Combined_Fields = Table()for each_field in fields:    catalog = ascii.read('/Users/laurenhiggins/Dropbox/GEG_Research/Key_Parameters_Catalogs_Per_Field/Stellar_Mass_Catalogs_old/CANDELS.%s.F160W.v1.mass.cat'%each_field)    Msol_subset = catalog[['Seq', 'RAdeg', 'DECdeg', 'M_med', 's_med', 'M_neb_med', 's_neb_med']]    Msol_subset['Field_flag'] = each_field    Msol_Combined_Fields = vstack([Msol_Combined_Fields, Msol_subset])ascii.write(Msol_Combined_Fields, '/Users/laurenhiggins/Dropbox/GEG_Research/Key_Parameters_Catalogs_Per_Field/Stellar_Mass_Catalogs_old/CANDELS.ALL.FIELDS.F160W.v1.mass.cat',             names=('Seq', 'RAdeg', 'DECdeg', 'M_med', 's_med', 'M_neb_med', 's_neb_med', 'Field_flag'), format='csv', overwrite=True)'''Key Column HeadersSeq RAdeg DECdeg M_med s_med M_neb_med s_neb_med'''