from astropy.table import vstack, Tablefrom astropy.io import asciifields = ['COSMOS', 'EGS', 'GOODSN', 'GOODSS', 'UDS']VisClass_Combined_Fields = Table()for each_field in fields:    catalog = ascii.read('/Users/laurenhiggins/Dropbox/GEG_Research/Key_Parameters_Catalogs_Per_Field/Visual_Classifications_Catalogs/CANDELS.%s.F160W_v3_metrics.csv'%each_field)    VisClass_subset = catalog[['RA', 'Dec', 'Dv', 'MCV', 'DSw', 'Cw', 'IPw', 'UNw', 'VKw', 'ZKw', 'JKw', 'Cv']]    VisClass_subset['Field_flag'] = each_field    VisClass_Combined_Fields = vstack([VisClass_Combined_Fields, VisClass_subset])ascii.write(VisClass_Combined_Fields, '/Users/laurenhiggins/Dropbox/GEG_Research/Key_Parameters_Catalogs_Per_Field/Visual_Classifications_Catalogs/CANDELS.ALL.FIELDS.F160W_v3_metrics.cat',             names=('RA', 'Dec', 'Dv', 'MCV', 'DSw', 'Cw', 'IPw', 'UNw', 'VKw', 'ZKw', 'JKw', 'Cv','Field_Flag'), format='csv', overwrite=True)'''Notes for Jan 6th - Look over draft to see what columns are necessary for the paper.'RA', 'Dec', 'Dv', 'MCV', 'DSw', 'Cw', 'IPw', 'UNw', 'VKw', 'ZKw', 'JKw', 'Cv''''